.mdui-slider input[type="range"] {
  position: absolute;
  top: 50%;
  z-index: 2;
  width: 100%;
  height: 20px;
  -webkit-appearance: none;
  margin-top: -10px;
  cursor: pointer;
  opacity: 0;

     -moz-appearance: none;
          appearance: none;
}
.mdui-slider-track,
.mdui-slider-fill {
  position: absolute;
  top: 50%;
  height: 2px;
  -webkit-box-sizing: border-box;
          box-sizing: border-box;
  margin-top: -1px;
