/**
 * =============================================================================
 * ************   Slider 滑块   ************
 * =============================================================================
 */
.mdui-slider {
  position: relative;
  display: block;
  width: 100%;
  height: 36px;
}
.mdui-slider input[type="range"] {
  position: absolute;
  top: 50%;
  z-index: 2;
  width: 100%;
  height: 20px;
