Page.content:
.nexmoe-archives .tagcloud {
  margin-bottom: 13px;
  font-size: 0;
}
.nexmoe-archives .categories {
  margin-bottom: 12px;
  font-size: 0;
}
.nexmoe-archives .tagcloud a {
  border-radius: 15px;
  padding: 6px 12px;
  font-size: 15px !important;
  display: inline-block;
  margin-bottom: 7px;
  margin-right: 7px;
}
.nexmoe-archives .tagcloud a:before {
  content: "# ";
}
.nexmoe-archives .tagcloud a span:before {
  content: " ";
}
.nexmoe-archives .categories a {
  border-radius: 12px;
  padding: 12px;
  font-size: 16px;
  display: inline-block;
  margin-bottom: 8px;
  margin-right: 8px;
  width: calc(20% - 6.4px);
}
.nexmoe-archives .categories a:nth-child(5n+5) {
  margin-right: 0;
}
.nexmoe-archives .categories a span {
  pointer-events: none;
}
@media screen and (max-width: 768px) {
  .nexmoe-archives .categories a {
    width: calc(50% - 4px);
  }
  .nexmoe-archives .categories a:nth-child(2n+2) {
    margin-right: 0;
  }
  .nexmoe-archives .categories a:nth-child(5n+5) {
    margin-right: 8px;
  }
}
.nexmoe-archives .categories a span {
  margin-top: 5px;
  display: block;
}
.nexmoe-archives .categories a span:after {
  content: " Articles";
}
.nexmoe-archives article > *:first-child {
  margin-top: 0;
}
.nexmoe-archives article > *:last-child {
  margin-bottom: 0;
}
.nexmoe-archives ul {
  list-style: none;
  padding-left: 0 !important;
}
.nexmoe-archives li {
  position: relative;
  padding: 10px 0;
  padding-left: 25px;
}
.nexmoe-archives li::before {
  content: "";
  width: 14px;
  height: 14px;
  background: LightSkyBlue;
  display: inline-block;
  vertical-align: middle;
  margin-top: -2px;
  margin-right: 11px;
  margin-left: -25px;
  border-radius: 100%;
  border: 3px solid #fff;
  z-index: 100;
  position: relative;
}
.nexmoe-archives li::after {
  content: "";
  height: 100%;
  width: 2px;
  background: rgba(255,78,106,0.2);
  position: absolute;
  left: 6px;
  top: 20px;
}
.nexmoe-archives li:last-child:after {
  display: none;
}
.nexmoe-archives ul span {
  margin-right: 15px;
}
article {
  color: #444;
  font-family: microsoft yahei;
}
article p {
  line-height: 24px;
}
article code {
  background-color: rgba(27,31,35,0.05);
  border-radius: 10px;
  font-size: 85%;
  padding: 0.2em 0.4em;
}
article code::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}
article code::-webkit-scrollbar-thumb {
  border-radius: 10px;
  background-color: #888;
}
article pre {
  border-radius: 13px;
  overflow: hidden;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  margin: 25px 0;
  padding: 15px;
  position: relative;
  background: #282c34;
  line-height: 1.3em;
}
article pre::before {
  background: #282c34;
  height: 32px;
  margin-bottom: 0;
  display: block;
}
article pre:after {
  position: absolute;
  border-radius: 50%;
  background: DeepSkyBlue;
  width: 12px;
  height: 12px;
  top: 0;
  left: 12px;
  margin-top: 12px;
  -webkit-box-shadow: 20px 0 #ffbd2e, 40px 0 #27c93f;
  box-shadow: 20px 0 #ffbd2e, 40px 0 #27c93f;
}
article pre code {
  position: relative;
  border: none;
  border-radius: 0;
  font-size: 100%;
  font-family: Consolas;
}
article pre code:after {
  color: #5c6370;
  position: absolute;
  top: 0.5em;
  bottom: 0.5em;
  left: 0;
  text-align: left;
  clip: rect(-100px 2em 9999px 0);
  clip: rect(-100px 5ch 9999px 0);
  overflow: hidden;
  padding: 1px 0;
  padding-left: 12px;
  background: #282c34;
}
article a {
  -webkit-transition: all 0.35s ease-out;
  transition: all 0.35s ease-out;
  color: LightSkyBlue;
  text-decoration: none;
  word-wrap: break-word;
}
article a:hover,
article a:focus {
  color: LightSkyBlue;
}
article a:hover {
  text-decoration: underline;
}
article p {
  margin: 25px 0;
  position: relative;
}
article p:after {
  content: "";
  clear: both;
  display: block;
}
article del {
  color: #999;
  font-size: 0.9em;
}
article table.nexmoe-album {
  margin: -5px;
  max-width: calc(100% + 10px);
}
article table.nexmoe-album + .nexmoe-album {
  margin-top: 5px;
}
article table.nexmoe-album,
article table.nexmoe-album thead,
article table.nexmoe-album th,
article table.nexmoe-album td {
  border: none;
  box-shadow: none;
}
article table.nexmoe-album * {
  background-color: transparent !important;
}
article table.nexmoe-album th,
article table.nexmoe-album td {
  padding: 5px;
  height: 300px;
}
article table.nexmoe-album th a:after,
article table.nexmoe-album td a:after {
  display: none;
}
@media screen and (max-width: 1024px) {
  article table.nexmoe-album th,
  article table.nexmoe-album td {
    height: 100px;
  }
}
article table.nexmoe-album img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.justified-gallery {
  margin: -10px;
  width: calc(100% + 20px);
}
.jg-entry {
  border-radius: 10px;
  overflow: hidden;
}
article img {
  display: block;
  border-radius: 13px;
}
article img.lazyload,
article img.lazyloading {
  opacity: 0;
  transition: opacity 400ms ease-out;
}
article img.lazyloaded {
  opacity: 1;
}
article h1,
article h2,
article h3,
article h4,
article h5,
article h6 {
  color: #111;
  margin: 25px 0;
}
article h4,
article h5,
article h6 {
  position: relative;
}
article h4:before,
article h5:before,
article h6:before {
  display: inline-block;
  width: 1em;
  content: '#';
  color: LightSkyBlue;
}
article h1 {
  font-size: 2.5em;
}
article h2 {
  font-size: 1.7em;
  position: relative;
  padding-bottom: 10px;
}
article h2:before {
  content: "";
  width: 100%;
  padding: 0px 25px;
  border-bottom: 1px solid #eee;
  position: absolute;
  bottom: -1px;
  left: -25px;
  box-sizing: unset;
}
article h2:after {
  transition: all 0.35s ease-out;
  content: "";
  position: absolute;
  background: LightSkyBlue;
  width: 1em;
  height: 5px;
  bottom: -3px;
  left: 0;
  border-radius: 10px;
}
article h2:hover:after {
  width: 2em;
}
article h3 {
  font-size: 1.3em;
}
article h4 {
  font-size: 1.15em;
}
article h5 {
  font-size: 1em;
}
article h6 {
  font-size: 0.9em;
}
article blockquote {
  color: #666;
  margin: 25px 0;
  padding: 0.1em 2.2em;
  background-color: #f8f8f8;
  line-height: 28px;
  position: relative;
  border-radius: 10px;
  border: 1px solid #eee;
  font-family: sf pro sc, sf pro display, sf pro icons, aos icons, pingfang sc, helvetica neue, helvetica, arial, sans-serif;
}
article blockquote:before {
  content: "\201C";
  top: 15px;
  left: 10px;
  position: absolute;
  color: #888;
  font-size: 3em;
}
article blockquote:after {
  content: "\201D";
  bottom: -8px;
  right: 10px;
  position: absolute;
  color: #888;
  font-size: 3em;
}
article hr {
  display: block;
  border: 0;
  border-top: 1px dashed #ccc;
  margin: 25px 0;
  padding: 0;
}
article sub,
article sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
